import React from "react"
import { Route } from 'react-router-dom'

import { connect } from 'react-redux'
import { fetchCollectionsStartAsync } from '../../redux/shop/shop.actions'

// pages/components
import CollectionPage from "../collection/collection.component"
import CollectionsOverviewContainer from '../../components/collections-overview/collections-overview.container'

//hocs
import WithSpinner from '../../components/with-spinner/with-spinner.component'

//selectors
import { selectIsCollectionFetching, selectCollectionsLoaded } from '../../redux/shop/shop.selectors'

// =+=+=+=+=+=+=+=+=+=+=+=+=+=+=+=+=+=+=+=+

const CollectionPageWithSpinner = WithSpinner(CollectionPage)

class ShopPage extends React.Component{

	componentDidMount(){
		const { fetchCollectionsStartAsync } = this.props
		fetchCollectionsStartAsync()
	}

	render(){

		const {match, isCollectionFetching, isCollectionsLoaded} = this.props

		return(
			<div className="shop-page">
				<Route 
					exact 
					path={`${match.path}`} 
					render={props => 
						<CollectionsOverviewContainer {...props} />} 
				/>

				<Route 
						path={`${match.path}/:collectionId`} 
						render={props =>
							<CollectionPageWithSpinner isLoading={!isCollectionsLoaded} {...props} />} 
				/>

			</div>
		)
	}
}

const mapStateToProps = state => ({
	isCollectionFetching: selectIsCollectionFetching(state),
	isCollectionsLoaded: selectCollectionsLoaded(state)
})

const mapDispatchToProps = dispatch => ({
	fetchCollectionsStartAsync: () => dispatch(fetchCollectionsStartAsync())
})

export default connect(mapStateToProps, mapDispatchToProps)(ShopPage)
